mapper.select:
		select count(*) 
  		from rec_reply
  		where b_no=#{b_no}

SELECT b_no,title,nickname,regdate,updatedate,boomup,intro,cooktip,viewcnt,star,mno,replycnt, SUBSTR(videoLink, -11) AS videoCode
FROM rec_board
WHERE b_no = #{b_no}

mapper.update:

update rec_board
	set viewcnt = viewcnt + 1
where b_no = #{b_no}
